SELECT bd_number, bd_type, us_id, bd_guest_nickname, bd_title, bd_content, bd_register_date, bd_update_date, bd_view_count FROM ( SELECT /*+ INDEX_DESC(board_table pk_bd_number) */ ROWNUM rn, bd_number, bd_type, us_id, bd_title, bd_content, bd_register_date, bd_update_date, bd_view_count FROM board_table WHERE
bd_type = #{bd_type} AND
ROWNUM <= #{cri.pageNum} * #{cri.amount} ) WHERE rn > (#{cri.pageNum} - 1) * #{cri.amount}
